import React, { useState, useEffect } from 'react';
import styled, { keyframes } from 'styled-components';
import { CelestialBody, Flower, EmotionType } from '../types';
import {
  moonImage,
  starIcon,
  meteorIcon,
  flower1Icon,
  flower2Icon,
  flower3Icon
} from '../assets';

// 旋转动画
const rotate = keyframes`
  from { transform: rotate(0deg); }
  to   { transform: rotate(360deg); }
`;

// 浮动动画
const float = keyframes`
  0%   { transform: translateY(0); }
  50%  { transform: translateY(-10px); }
  100% { transform: translateY(0); }
`;

const MoonContainer = styled.div`
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 300px;
  height: 300px;
  z-index: 1;
`;

const MoonSphere = styled.div`
  width: 100%; height: 100%;
  border-radius: 50%;
  background: url(${moonImage}) no-repeat center center;
  background-size: cover;
  animation: ${rotate} 60s linear infinite;
  box-shadow: inset -25px -25px 40px rgba(0,0,0,0.3);
`;

const CelestialBodyContainer = styled.div<{ x: number; y: number; type: EmotionType }>`
  position: absolute;
  left: ${p => p.x}px; top: ${p => p.y}px;
  width: 48px; height: 48px;
  background-size: contain;
  background-repeat: no-repeat;
  background-position: center;
  background-image: ${p =>
    p.type === 'positive' ? `url(${starIcon})`
    : p.type === 'negative' ? `url(${meteorIcon})`
    : `url(${starIcon})`};
  animation: ${float} 6s ease-in-out infinite;
  cursor: pointer;
  filter: ${p => p.type === 'received' ? 'hue-rotate(90deg)' : 'none'};
  &:hover {
    transform: scale(1.2);
    filter: brightness(1.2) ${p => p.type === 'received' ? 'hue-rotate(90deg)' : ''};
    z-index: 10;
  }
`;

const FlowerContainer = styled.div<{ x: number; y: number; style: string }>`
  position: absolute;
  left: ${p => p.x}px; top: ${p => p.y}px;
  width: 32px; height: 32px;
  background-size: contain;
  background-repeat: no-repeat;
  background-position: center;
  background-image: ${p => {
    switch(p.style) {
      case 'flower1': return `url(${flower1Icon})`;
      case 'flower2': return `url(${flower2Icon})`;
      case 'flower3': return `url(${flower3Icon})`;
      default: return `url(${flower1Icon})`;
    }
  }};
`;

const Tooltip = styled.div`
  position: absolute;
  background: rgba(0,0,0,0.8);
  color: #fff;
  padding: 6px 10px;
  border-radius: 4px;
  font-size: 12px;
  max-width: 180px;
  pointer-events: none;
  opacity: 0; transition: opacity 0.2s;
  ${CelestialBodyContainer}:hover & { opacity: 1; }
`;

interface MoonProps {
  celestialBodies: CelestialBody[];
  flowers?: Flower[];
}

export const Moon: React.FC<MoonProps> = ({ celestialBodies, flowers = [] }) => {
  const [positions, setPositions] = useState<Record<string,{x:number,y:number}>>({});

  useEffect(() => {
    // 获取准确的浏览器可视区域大小
    const w = document.documentElement.clientWidth;
    const h = document.documentElement.clientHeight;

    // 月球中心和半径
    const moonX = w / 2;
    const moonY = h / 2;
    const moonR = 150;

    // 定义边距
    const margin = 50;
    const topGap = 100;   // 顶部控制栏高度
    const bottomGap = 150;// 底部输入区高度

    const minX = margin;
    const maxX = w - 48; // 右边距，确保不超过边缘
    const minY = topGap + margin;
    const maxY = h - bottomGap - margin;

    const newPos: typeof positions = {};
    celestialBodies.forEach(body => {
      if (positions[body.id]) {
        newPos[body.id] = positions[body.id];
      } else {
        let x: number, y: number, cnt = 0;
        do {
          // 使用可视区域来计算随机位置
          x = Math.random() * (maxX - minX) + minX;
          y = Math.random() * (maxY - minY) + minY;
          cnt++;
        } while (
          cnt < 100 &&
          Math.hypot(x - moonX, y - moonY) < (moonR + 60) // 确保远离月球
        );
        newPos[body.id] = { x, y };
      }
    });
    setPositions(newPos);
  }, [celestialBodies]);

  return (
    <MoonContainer>
      <MoonSphere>
        {flowers.map(f => (
          <FlowerContainer
            key={f.id}
            x={f.position.x}
            y={f.position.y}
            style={f.style}
          />
        ))}
      </MoonSphere>
      {celestialBodies.map(body => (
        <CelestialBodyContainer
          key={body.id}
          x={positions[body.id]?.x || 0}
          y={positions[body.id]?.y || 0}
          type={body.type}
        >
          <Tooltip>{body.content}</Tooltip>
        </CelestialBodyContainer>
      ))}
    </MoonContainer>
  );
};
