import React, { useEffect } from 'react';
import styled, { keyframes } from 'styled-components';

const fadeInOut = keyframes`
  0% {
    opacity: 0;
    transform: translate(-50%, -50%);
  }
  20% {
    opacity: 1;
    transform: translate(-50%, -50%);
  }
  80% {
    opacity: 1;
    transform: translate(-50%, -50%);
  }
  100% {
    opacity: 0;
    transform: translate(-50%, -50%);
  }
`;

const MessageContainer = styled.div`
  position: fixed;
  top: 20%;
  left: 50%;
  transform: translate(-50%, -50%);
  z-index: 1000;
  animation: ${fadeInOut} 6s ease-in-out forwards;

  padding: 15px 25px;
  color: rgba(255, 255, 255, 0.95);
  font-size: 16px;
  letter-spacing: 1px;
  text-align: center;
  white-space: nowrap;
  text-shadow: 0 0 10px rgba(255, 255, 255, 0.5);

  /* 移除所有背景和边框 */
  background: none;
  border: none;
  box-shadow: none;
  backdrop-filter: none;

  /* 添加轻微的光晕效果 */
  &::before {
    content: '';
    position: absolute;
    top: 50%;
    left: 50%;
    width: 100%;
    height: 100%;
    transform: translate(-50%, -50%);
    background: radial-gradient(
      circle,
      rgba(255, 255, 255, 0.1) 0%,
      rgba(255, 255, 255, 0) 70%
    );
    z-index: -1;
    pointer-events: none;
  }
`;


interface ResponseMessageProps {
  onAnimationEnd: () => void;
  type?: 'reply' | 'radio';
}

export const ResponseMessage: React.FC<ResponseMessageProps> = ({ onAnimationEnd, type }) => {
  useEffect(() => {
    const timer = setTimeout(() => {
      onAnimationEnd();
    }, 5000);

    return () => clearTimeout(timer);
  }, [onAnimationEnd]);

  let message = '你的回应，正随着微光，穿越宇宙飞向他人💫';
  if (type === 'radio') {
    message = '电台已开启📻，准备接收来自宇宙深处的心声';
  }

  return (
    <MessageContainer>
      {message}
    </MessageContainer>
  );
};
