import Sentiment from 'sentiment';
import { EmotionType } from '../types';

export class EmotionAnalyzer {
  private analyzer: Sentiment;
  private static instance: EmotionAnalyzer;
  private lastAnalysis: { text: string; result: EmotionType } | null = null;
  private analysisHistory: Array<{ text: string; result: EmotionType }> = [];

  private constructor() {
    this.analyzer = new Sentiment();

    // 添加中文情感词典
    this.analyzer.registerLanguage('zh', {
      // 积极情感词
      labels: {
        '开心': 2,
        '快乐': 2,
        '高兴': 2,
        '喜欢': 2,
        '爱': 2,
        '棒': 2,
        '好': 1,
        '优秀': 2,
        '感恩': 2,
        '希望': 1,
        '加油': 2,
        '美好': 2,
        '温暖': 2,
        '厉害': 2,
        '出色': 2,
        '精彩': 2,
        '完美': 2,
        '成功': 2,
        '幸福': 2,
        '可爱': 1,
        '赞': 1,
        '帅': 1,
        '酷': 1,
        '牛': 1,
        '强': 1
      },
      // 消极情感词
      negators: {
        '难过': -2,
        '伤心': -2,
        '痛苦': -2,
        '讨厌': -2,
        '烦': -2,
        '糟糕': -2,
        '失望': -2,
        '焦虑': -2,
        '生气': -2,
        '疲惫': -1,
        '孤独': -2,
        '压力': -1,
        '担心': -1,
        '害怕': -2,
        '恐惧': -2,
        '悲伤': -2,
        '绝望': -2,
        '崩溃': -2,
        '痛': -1,
        '累': -1,
        '怕': -1,
        '惨': -2,
        '差': -1,
        '弱': -1
      }
    });
  }

  public static getInstance(): EmotionAnalyzer {
    if (!EmotionAnalyzer.instance) {
      EmotionAnalyzer.instance = new EmotionAnalyzer();
    }
    return EmotionAnalyzer.instance;
  }

  private adjustScoreBasedOnContext(text: string, score: number): number {
    let adjustedScore = score;

    // 1. 检查表情符号
    const positiveEmojis = ['😊', '😄', '😃', '😀', '😁', '😆', '😅', '😂', '🤣', '❤️', '🥰', '✨'];
    const negativeEmojis = ['😢', '😭', '😞', '😔', '😟', '😕', '😣', '😖', '😫', '😩', '😱', '😨'];

    positiveEmojis.forEach(emoji => {
      if (text.includes(emoji)) adjustedScore += 2;
    });
    negativeEmojis.forEach(emoji => {
      if (text.includes(emoji)) adjustedScore -= 2;
    });

    // 2. 检查标点符号
    const exclamationCount = (text.match(/!|！/g) || []).length;
    if (adjustedScore > 0) {
      adjustedScore += exclamationCount; // 正面情感的感叹号加强正面效果
    } else if (adjustedScore < 0) {
      adjustedScore -= exclamationCount; // 负面情感的感叹号加强负面效果
    }

    // 3. 检查程度词
    const degreeWords = {
      '非常': 2,
      '很': 1.5,
      '特别': 1.5,
      '真': 1.5,
      '太': 1.5,
      '超': 1.5,
      '好': 1
    };

    Object.entries(degreeWords).forEach(([word, value]) => {
      if (text.includes(word)) {
        adjustedScore = adjustedScore > 0 ? adjustedScore + value : adjustedScore - value;
      }
    });

    // 4. 考虑历史分析结果（情感连续性）
    if (this.lastAnalysis && this.lastAnalysis.result === 'positive') {
      adjustedScore += 0.5;
    } else if (this.lastAnalysis && this.lastAnalysis.result === 'negative') {
      adjustedScore -= 0.5;
    }

    return adjustedScore;
  }

  public analyzeEmotion(text: string): EmotionType {
    try {
      // 1. 获取基础分数
      const result = this.analyzer.analyze(text);
      let score = result.score;

      // 2. 根据上下文调整分数
      score = this.adjustScoreBasedOnContext(text, score);

      // 3. 确定情感类型
      let emotionType: EmotionType;
      if (score > 0) {
        emotionType = 'positive';
      } else if (score < 0) {
        emotionType = 'negative';
      } else {
        // 对于中性情感，我们根据更多特征来判断
        const hasPositiveWords = result.positive.length > 0;
        const hasNegativeWords = result.negative.length > 0;

        if (hasPositiveWords && !hasNegativeWords) {
          emotionType = 'positive';
        } else if (hasNegativeWords && !hasPositiveWords) {
          emotionType = 'negative';
        } else {
          // 如果还是无法判断，保持积极倾向
          emotionType = 'positive';
        }
      }

      // 4. 更新分析历史
      this.lastAnalysis = { text, result: emotionType };
      this.analysisHistory.push({ text, result: emotionType });
      if (this.analysisHistory.length > 10) {
        this.analysisHistory.shift();
      }

      return emotionType;
    } catch (error) {
      console.error('情感分析出错:', error);
      // 如果分析出错，根据历史记录返回一个可能的结果
      if (this.lastAnalysis) {
        return this.lastAnalysis.result;
      }
      // 如果没有历史记录，返回积极
      return 'positive';
    }
  }

  // 用于调试和优化的方法
  public getAnalysisStats() {
    return {
      historyLength: this.analysisHistory.length,
      positiveCount: this.analysisHistory.filter(item => item.result === 'positive').length,
      negativeCount: this.analysisHistory.filter(item => item.result === 'negative').length,
      lastAnalysis: this.lastAnalysis
    };
  }
}
