import React, { useState } from 'react';
import styled from 'styled-components';
import { Flower } from '../types';
import { flower1Icon, flower2Icon, flower3Icon } from '../assets';

const FlowerSelectPanel = styled.div`
  position: fixed;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  background: rgba(20, 20, 40, 0.8);
  border-radius: 15px;
  padding: 20px;
  display: flex;
  flex-direction: column;
  gap: 15px;
  z-index: 1000;
  backdrop-filter: blur(5px);
  box-shadow: 0 5px 20px rgba(0, 0, 0, 0.3);
`;

const Title = styled.h3`
  color: white;
  text-align: center;
  margin: 0 0 10px 0;
  font-size: 18px;
`;

const FlowerOptions = styled.div`
  display: flex;
  justify-content: space-around;
  gap: 15px;
`;

const FlowerOption = styled.div<{ selected: boolean }>`
  width: 60px;
  height: 60px;
  background-size: contain;
  background-repeat: no-repeat;
  background-position: center;
  cursor: pointer;
  transition: all 0.2s;
  border-radius: 10px;
  padding: 10px;
  background-color: ${props => props.selected ? 'rgba(255, 255, 255, 0.2)' : 'transparent'};

  &:hover {
    transform: scale(1.1);
    background-color: rgba(255, 255, 255, 0.1);
  }
`;

const DraggableFlower = styled.div<{ x: number; y: number; flowerType: string; isDragging: boolean }>`
  position: fixed;
  left: ${props => props.x}px;
  top: ${props => props.y}px;
  width: 48px;
  height: 48px;
  background-size: contain;
  background-repeat: no-repeat;
  background-position: center;
  background-image: ${props => {
    switch (props.flowerType) {
      case 'flower1': return `url(${flower1Icon})`;
      case 'flower2': return `url(${flower2Icon})`;
      case 'flower3': return `url(${flower3Icon})`;
      default: return `url(${flower1Icon})`;
    }
  }};
  cursor: ${props => props.isDragging ? 'grabbing' : 'grab'};
  z-index: ${props => props.isDragging ? 1001 : 1000};
  opacity: ${props => props.isDragging ? 0.8 : 1};
  filter: drop-shadow(0 2px 4px rgba(0, 0, 0, 0.3));
  transition: transform 0.1s;

  &:hover {
    transform: ${props => props.isDragging ? 'none' : 'scale(1.1)'};
  }
`;

const ConfirmButton = styled.button`
  background: rgba(60, 120, 255, 0.5);
  color: white;
  border: none;
  padding: 8px 15px;
  border-radius: 20px;
  cursor: pointer;
  margin-top: 10px;
  transition: all 0.2s;

  &:hover {
    background: rgba(80, 140, 255, 0.7);
  }
`;

interface FlowerSelectorProps {
  onPlantFlower: (flower: Flower) => void;
  onClose: () => void;
}

export const FlowerSelector: React.FC<FlowerSelectorProps> = ({ onPlantFlower, onClose }) => {
  const [selectedFlower, setSelectedFlower] = useState<'flower1' | 'flower2' | 'flower3'>('flower1');
  const [dragging, setDragging] = useState(false);
  const [dragPosition, setDragPosition] = useState({ x: 0, y: 0 });
  const [dragStarted, setDragStarted] = useState(false);

  // 处理花朵选择
  const handleFlowerSelect = (type: 'flower1' | 'flower2' | 'flower3') => {
    setSelectedFlower(type);
  };

  // 开始拖拽
  const handleFlowerOptionClick = () => {
    setDragStarted(true);
    // 初始位置设为面板中心
    const panel = document.querySelector('div[class^="FlowerSelectPanel"]');
    if (panel) {
      const rect = panel.getBoundingClientRect();
      setDragPosition({
        x: rect.x + rect.width / 2 - 24,
        y: rect.y + rect.height / 2 - 24
      });
    } else {
      // 如果找不到面板，使用屏幕中心
      setDragPosition({
        x: window.innerWidth / 2 - 24,
        y: window.innerHeight / 2 - 24
      });
    }
  };

  // 用于拖拽花朵
  const handleMouseDown = (e: React.MouseEvent) => {
    e.preventDefault();
    e.stopPropagation();
    setDragging(true);

    const handleMouseMove = (moveEvent: MouseEvent) => {
      moveEvent.preventDefault();
      setDragPosition({
        x: moveEvent.clientX - 24, // 24是花朵宽度的一半
        y: moveEvent.clientY - 24, // 24是花朵高度的一半
      });
    };

    const handleMouseUp = (upEvent: MouseEvent) => {
      upEvent.preventDefault();
      setDragging(false);
      document.removeEventListener('mousemove', handleMouseMove);
      document.removeEventListener('mouseup', handleMouseUp);

      // 创建并添加花朵到月球
      const newFlower: Flower = {
        id: `flower-${Date.now()}`,
        style: selectedFlower,
        position: {
          x: Math.round(upEvent.clientX - 24), // 向下取整，确保为整数
          y: Math.round(upEvent.clientY - 24),
        }
      };

      onPlantFlower(newFlower);
      onClose(); // 关闭选择面板
    };

    document.addEventListener('mousemove', handleMouseMove);
    document.addEventListener('mouseup', handleMouseUp);
  };

  // 直接放置花朵
  const handleConfirm = () => {
    // 计算月球中心位置（假设在屏幕中央）
    const centerX = Math.round(window.innerWidth / 2);
    const centerY = Math.round(window.innerHeight / 2);

    // 随机位置（在月球附近）
    const randomAngle = Math.random() * Math.PI * 2;
    const randomDistance = 50 + Math.random() * 80; // 距离月球中心50-130px

    const x = Math.round(centerX + Math.cos(randomAngle) * randomDistance - 24);
    const y = Math.round(centerY + Math.sin(randomAngle) * randomDistance - 24);

    const newFlower: Flower = {
      id: `flower-${Date.now()}`,
      style: selectedFlower,
      position: { x, y }
    };

    onPlantFlower(newFlower);
    onClose();
  };

  return (
    <>
      <FlowerSelectPanel>
        <Title>选择花朵样式</Title>
        <FlowerOptions>
          <FlowerOption 
            selected={selectedFlower === 'flower1'}
            onClick={() => {
              handleFlowerSelect('flower1');
              handleFlowerOptionClick();
            }}
            style={{ backgroundImage: `url(${flower1Icon})` }}
          />
          <FlowerOption 
            selected={selectedFlower === 'flower2'}
            onClick={() => {
              handleFlowerSelect('flower2');
              handleFlowerOptionClick();
            }}
            style={{ backgroundImage: `url(${flower2Icon})` }}
          />
          <FlowerOption 
            selected={selectedFlower === 'flower3'}
            onClick={() => {
              handleFlowerSelect('flower3');
              handleFlowerOptionClick();
            }}
            style={{ backgroundImage: `url(${flower3Icon})` }}
          />
        </FlowerOptions>
        <div style={{ textAlign: 'center', color: 'white', margin: '5px 0', fontSize: '14px' }}>
          选择花朵后开始拖拽，或者点击
        </div>
        <ConfirmButton onClick={handleConfirm}>
          直接放置
        </ConfirmButton>
      </FlowerSelectPanel>

      {dragStarted && (
        <DraggableFlower 
          x={dragPosition.x}
          y={dragPosition.y}
          flowerType={selectedFlower}
          isDragging={dragging}
          onMouseDown={handleMouseDown}
        />
      )}
    </>
  );
};
