// src/components/FlowerSelector.tsx
import React, { useState } from 'react';
import styled from 'styled-components';
import { Flower } from '../types';
import { flower1Icon, flower2Icon, flower3Icon } from '../assets';

const FlowerSelectPanel = styled.div`
  position: fixed;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  background: rgba(20, 20, 40, 0.8);
  border-radius: 15px;
  padding: 20px;
  display: flex;
  flex-direction: column;
  gap: 15px;
  z-index: 1000;
  backdrop-filter: blur(5px);
  box-shadow: 0 5px 20px rgba(0, 0, 0, 0.3);
`;

const Title = styled.h3`
  color: white;
  text-align: center;
  margin: 0 0 10px;
  font-size: 18px;
`;

const FlowerOptions = styled.div`
  display: flex;
  justify-content: space-around;
  gap: 15px;
`;

const FlowerOption = styled.div<{
  selected: boolean;
  flowerType: 'flower1' | 'flower2' | 'flower3';
}>`
  width: 60px;
  height: 60px;
  background-size: contain;
  background-repeat: no-repeat;
  background-position: center;
  cursor: pointer;
  transition: all 0.2s;
  border-radius: 10px;
  padding: 10px;
  background-color: ${({ selected }) =>
    selected ? 'rgba(255, 255, 255, 0.2)' : 'transparent'};
  background-image: ${({ flowerType }) => {
    switch (flowerType) {
      case 'flower1':
        return `url(${flower1Icon})`;
      case 'flower2':
        return `url(${flower2Icon})`;
      case 'flower3':
        return `url(${flower3Icon})`;
    }
  }};

  &:hover {
    transform: scale(1.1);
    background-color: rgba(255, 255, 255, 0.1);
  }
`;

const Description = styled.div`
  text-align: center;
  color: white;
  margin: 5px 0 0;
  font-size: 14px;
`;

interface FlowerSelectorProps {
  onPlantFlower: (flower: Flower) => void;
  onClose: () => void;
}

export const FlowerSelector: React.FC<FlowerSelectorProps> = ({
  onPlantFlower,
  onClose,
}) => {
  const [selectedFlower, setSelectedFlower] = useState<
    'flower1' | 'flower2' | 'flower3'
  >('flower1');

  // 计算并放置新花朵
  const placeFlowerOnMoon = (
    style: 'flower1' | 'flower2' | 'flower3'
  ) => {
    const centerX = Math.round(window.innerWidth / 2);
    const centerY = Math.round(window.innerHeight / 2);

    // 随机角度与距离（50~130px）
    const angle = Math.random() * Math.PI * 2;
    const distance = 50 + Math.random() * 80;
    const x = Math.round(centerX + Math.cos(angle) * distance - 16); // 32px/2
    const y = Math.round(centerY + Math.sin(angle) * distance - 16);

    const newFlower: Flower = {
      id: `flower-${Date.now()}`,
      style,
      position: { x, y },
    };

    onPlantFlower(newFlower);
    onClose();
  };

  const handleSelectAndPlant = (
    flowerType: 'flower1' | 'flower2' | 'flower3'
  ) => {
    setSelectedFlower(flowerType);
    placeFlowerOnMoon(flowerType);
  };

  return (
    <FlowerSelectPanel>
      <Title>选择要种植的花朵</Title>
      <FlowerOptions>
        <FlowerOption
          selected={selectedFlower === 'flower1'}
          flowerType="flower1"
          onClick={() => handleSelectAndPlant('flower1')}
        />
        <FlowerOption
          selected={selectedFlower === 'flower2'}
          flowerType="flower2"
          onClick={() => handleSelectAndPlant('flower2')}
        />
        <FlowerOption
          selected={selectedFlower === 'flower3'}
          flowerType="flower3"
          onClick={() => handleSelectAndPlant('flower3')}
        />
      </FlowerOptions>
      <Description>点击花朵即可种植到月球上</Description>
    </FlowerSelectPanel>
  );
};
