import React, { useState, useEffect, useMemo } from 'react';
import styled, { keyframes } from 'styled-components';
import { CelestialBody, Flower, EmotionType } from '../types';
import {
  moonImage,
  starIcon,
  meteorIcon,
  flower1Icon,
  flower2Icon,
  flower3Icon,
  othersIcon
} from '../assets';
import { ReplyBox } from './ReplyBox';

const rotate = keyframes`
  from { transform: rotate(0deg); }
  to   { transform: rotate(360deg); }
`;

const float = keyframes`
  0%   { transform: translateY(0) translateX(0); }
  50%  { transform: translateY(-8px) translateX(5px); }
  100% { transform: translateY(0) translateX(0); }
`;

const MoonContainer = styled.div`
  position: absolute;
  top: 50%; left: 50%;
  transform: translate(-50%, -50%);
  width: 360px; height: 360px;
  pointer-events: none;
`;

const MoonSphere = styled.div`
  width: 100%; height: 100%;
  border-radius: 50%;
  background: url(${moonImage}) no-repeat center center;
  background-size: cover;
  animation: ${rotate} 60s linear infinite;
  box-shadow: inset -25px -25px 40px rgba(0,0,0,0.3);
`;

const FlowerOverlay = styled.div`
  position: absolute;
  top: 0; left: 0;
  width: 100%; height: 100%;
  pointer-events: auto;
`;

interface FlowerDotProps {
  $x: number;
  $y: number;
  flowerType: 'flower1' | 'flower2' | 'flower3';
}
const FlowerDot = styled.div<FlowerDotProps>`
  position: absolute;
  left: ${p => p.$x}px;
  top: ${p => p.$y}px;
  width: 32px;
  height: 32px;
  background-size: contain;
  background-repeat: no-repeat;
  background-position: center;
  background-image: ${p =>
    p.flowerType === 'flower1'
      ? `url(${flower1Icon})`
      : p.flowerType === 'flower2'
      ? `url(${flower2Icon})`
      : `url(${flower3Icon})`};
`;

const CelestialBodyContainer = styled.div<{
  type: EmotionType;
  x: number;
  y: number;
  isExternal?: boolean;
  isReplied?: boolean;
}>`
  position: absolute;
  left: ${props => props.x}px;
  top: ${props => props.y}px;
  width: 48px;
  height: 48px;
  background-size: contain;
  background-repeat: no-repeat;
  cursor: pointer;
  transition: transform 0.3s ease-in-out;
  animation: ${props => props.isExternal && props.isReplied ? 'moveAway 5s ease-in-out forwards' : 'none'};

  ${props => {
    if (props.isExternal) {
      return `background-image: url(${othersIcon});`;
    }
    switch (props.type) {
      case 'positive':
        return `background-image: url(${starIcon});`;
      case 'negative':
        return `background-image: url(${meteorIcon});`;
      case 'received':
        return `background-image: url(${starIcon});`;
      default:
        return '';
    }
  }}

  &:hover {
    transform: scale(1.2);
    z-index: 10;
  }
`;

const Tooltip = styled.div`
  position: absolute;
  left: 50%;
  top: -40px;
  transform: translateX(-50%);
  background: rgba(0, 0, 0, 0.8);
  color: white;
  padding: 8px 12px;
  border-radius: 6px;
  font-size: 14px;
  white-space: pre-wrap;
  pointer-events: none;
  opacity: 0;
  transition: all 0.2s ease;
  z-index: 100;
  min-width: 120px;
  max-width: 200px;
  text-align: center;
  display: none;

  ${CelestialBodyContainer}:hover & {
    opacity: 1;
    display: block;
  }

  &::after {
    content: '';
    position: absolute;
    left: 50%;
    bottom: -5px;
    transform: translateX(-50%) rotate(45deg);
    width: 10px;
    height: 10px;
    background: rgba(0, 0, 0, 0.8);
  }
`;

const PositionWrapper = styled.div`
  position: fixed;
  left: 0;
  top: 0;
  width: 100vw;
  height: 100vh;
  pointer-events: none;
`;

const ResponseMessage = styled.div`
  position: fixed;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  background: rgba(0, 0, 0, 0.8);
  color: white;
  padding: 15px 25px;
  border-radius: 20px;
  z-index: 1000;
  animation: fadeInOut 5s ease-in-out forwards;

  @keyframes fadeInOut {
    0% {
      opacity: 0;
      transform: translate(-50%, -30%);
    }
    20% {
      opacity: 1;
      transform: translate(-50%, -50%);
    }
    80% {
      opacity: 1;
      transform: translate(-50%, -50%);
    }
    100% {
      opacity: 0;
      transform: translate(-50%, -70%);
    }
  }
`;

interface MoonProps {
  celestialBodies: CelestialBody[];
  setCelestialBodies: React.Dispatch<React.SetStateAction<CelestialBody[]>>;
  flowers?: Flower[];
  isRadioOn?: boolean;
}

export const Moon: React.FC<MoonProps> = ({
  celestialBodies,
  setCelestialBodies,
  flowers = [],
  isRadioOn = false
}) => {
  const [positions, setPositions] = useState<Record<string, { x: number; y: number }>>({});
  const [selectedCelestialBody, setSelectedCelestialBody] = useState<CelestialBody | null>(null);
  const [repliedBodies, setRepliedBodies] = useState<Set<string>>(new Set());
  const [showResponseMessage, setShowResponseMessage] = useState(false);

  // ‚úÖ ‰øÆÊ≠£‰∫ÜÂéüÊù•ÁöÑ moonCenter ËÆ°ÁÆóÈÄªËæë
  const moonCenter = useMemo(() => {
    const topOffset = 120;
    const bottomOffset = 180;
    return {
      x: window.innerWidth / 2,
      y: (window.innerHeight - topOffset - bottomOffset) / 2 + topOffset
    };
  }, []);

  useEffect(() => {
    // Ê£ÄÊµãÊñ∞Ê∑ªÂä†ÁöÑÂ§©‰Ωì
    celestialBodies.forEach(body => {
      if (!positions[body.id]) {
        setRepliedBodies(prev => new Set([...prev, body.id]));
        // 5ÁßíÂêéÁßªÈô§Êñ∞Â§©‰ΩìÊ†áËÆ∞
        setTimeout(() => {
          setRepliedBodies(prev => {
            const next = new Set(prev);
            next.delete(body.id);
            return next;
          });
        }, 5000);
      }
    });

    const w = window.innerWidth;
    const h = window.innerHeight;
    const moonR = 150;
    const celestialSize = 48;

    const moonX = moonCenter.x;
    const moonY = moonCenter.y;

    const topMargin = 120;
    const bottomMargin = 180;
    const sideMargin = 100;

    const minX = sideMargin;
    const maxX = w - sideMargin - celestialSize;
    const minY = topMargin;
    const maxY = h - bottomMargin - celestialSize;

    const safeDistance = moonR + 80;

    const newPos: Record<string, { x: number; y: number }> = {};
    celestialBodies.forEach(body => {
      if (positions[body.id]) {
        newPos[body.id] = positions[body.id];
      } else {
        let x: number, y: number, cnt = 0;
        let angle: number;
        do {
          angle = Math.random() * Math.PI * 2;
          const minDist = safeDistance;
          const maxDist = Math.min(
            Math.min(moonX - minX, maxX - moonX) * 1.2,
            Math.min(moonY - minY, maxY - moonY) * 2.7
          );
          const distance = minDist + Math.random() * (maxDist - minDist);
          x = moonX + Math.cos(angle) * distance;
          y = moonY + Math.sin(angle) * distance;
          x = Math.max(minX, Math.min(maxX, x));
          y = Math.max(minY, Math.min(maxY, y));
          cnt++;
        } while (
          cnt < 100 && (
            Object.values(newPos).some(pos =>
              Math.hypot(x - pos.x, y - pos.y) < celestialSize * 1.5
            )
          )
        );
        newPos[body.id] = { x, y };
      }
    });

    setPositions(newPos);
  }, [celestialBodies, moonCenter]);

  const handleCelestialBodyClick = (body: CelestialBody) => {
    if (body.isExternal && isRadioOn) {
      setSelectedCelestialBody(body);
    }
  };

  const handleReplySubmit = (replyText: string) => {
    if (selectedCelestialBody && replyText.trim()) {
      const updatedReplies = new Set([...Array.from(selectedCelestialBody.replies), replyText]);
      const updatedCelestialBodies = celestialBodies.map(body => {
        if (body.id === selectedCelestialBody.id) {
          return { ...body, replies: updatedReplies };
        }
        return body;
      });
      setCelestialBodies(updatedCelestialBodies);
      setRepliedBodies(prev => new Set([...prev, selectedCelestialBody.id]));
      // Âè™ÊúâÁâπÊÆäÊòü‰ΩìÂõûÂ§çÂêéÊâçÊòæÁ§∫ÊñáÊ°à
      setShowResponseMessage(true);
      setTimeout(() => {
        setShowResponseMessage(false);
      }, 5000);
    }
    setSelectedCelestialBody(null);
  };

  const handleReplyClose = () => {
    setSelectedCelestialBody(null);
  };

  const visibleBodies = celestialBodies.filter(
    body => !body.isExternal || (body.isExternal && isRadioOn)
  );

  return (
    <PositionWrapper>
      <MoonContainer>
        <MoonSphere />
        <FlowerOverlay>
          {flowers.map((flower, index) => (
            <FlowerDot
              key={flower.id}
              $x={flower.position.x}
              $y={flower.position.y}
              flowerType={flower.style === 'flower1' ? 'flower1' : flower.style === 'flower2' ? 'flower2' : 'flower3'}
            />
          ))}
        </FlowerOverlay>
      </MoonContainer>
      {visibleBodies.map(body => {
        const pos = positions[body.id];
        if (!pos) return null;
        const isReplied = repliedBodies.has(body.id);

        return (
          <CelestialBodyContainer
            key={body.id}
            type={body.type}
            x={pos.x}
            y={pos.y}
            isExternal={body.isExternal}
            isReplied={isReplied && body.isExternal}
            onClick={() => handleCelestialBodyClick(body)}
          >
            <Tooltip>
              <p style={{ margin: 0 }}>{body.content}</p>
              {body.isExternal && <p style={{ margin: '5px 0 0 0' }}>Êù•Ëá™Ôºö{body.sender}</p>}
            </Tooltip>
          </CelestialBodyContainer>
        );
      })}

      {selectedCelestialBody && (
        <ReplyBox
          content={selectedCelestialBody.content}
          onSubmit={handleReplySubmit}
          onClose={handleReplyClose}
          replies={Array.from(selectedCelestialBody.replies)}
        />
      )}

