import React, { useState, KeyboardEvent } from 'react';
import styled from 'styled-components';
import { Visibility } from '../types';
import { starIcon, sendIcon } from '../assets';

const InputContainer = styled.div`
  position: fixed;
  bottom: 20px;
  left: 50%;
  transform: translateX(-50%);
  display: flex;
  align-items: center;
  gap: 10px;
  background: rgba(255, 255, 255, 0.1);
  padding: 10px;
  border-radius: 20px;
  backdrop-filter: blur(5px);
`;

const StarButton = styled.button<{ isStarred: boolean }>`
  background: none;
  border: none;
  width: 24px;
  height: 24px;
  cursor: pointer;
  background-image: url(${starIcon});
  background-size: contain;
  background-repeat: no-repeat;
  background-position: center;
  opacity: ${props => props.isStarred ? 1 : 0.5};
  transition: all 0.3s;

  &:hover {
    opacity: 1;
    transform: scale(1.1);
  }
`;

const Input = styled.input`
  width: 300px;
  padding: 10px;
  border: none;
  border-radius: 15px;
  background: rgba(255, 255, 255, 0.2);
  color: white;
  font-size: 16px;

  &::placeholder {
    color: rgba(255, 255, 255, 0.5);
  }

  &:focus {
    outline: none;
    background: rgba(255, 255, 255, 0.3);
  }
`;

const VisibilitySelect = styled.select`
  background: rgba(255, 255, 255, 0.2);
  border: none;
  border-radius: 15px;
  padding: 10px;
  color: white;
  cursor: pointer;

  option {
    background: #1a1a1a;
    color: white;
  }
`;

const SendButton = styled.button`
  background: none;
  border: none;
  width: 32px;
  height: 32px;
  cursor: pointer;
  background-image: url(${sendIcon});
  background-size: contain;
  background-repeat: no-repeat;
  background-position: center;
  transition: transform 0.3s;

  &:hover {
    transform: scale(1.1);
  }
`;

interface InputSectionProps {
  onSubmit: (text: string, visibility: Visibility, isStarred: boolean) => void;
}

export const InputSection: React.FC<InputSectionProps> = ({ onSubmit }) => {
  const [text, setText] = useState('');
  const [isStarred, setIsStarred] = useState(false);
  const [visibility, setVisibility] = useState<Visibility>('private');

  const handleSubmit = () => {
    if (text.trim()) {
      onSubmit(text, visibility, isStarred);
      setText('');
      setIsStarred(false);
    }
  };

  const handleKeyPress = (e: KeyboardEvent<HTMLInputElement>) => {
    if (e.key === 'Enter') {
      handleSubmit();
    }
  };

  return (
    <InputContainer>
      <StarButton
        isStarred={isStarred}
        onClick={() => setIsStarred(!isStarred)}
        title="ÊÉÖÁª™ËÆ∞ÂΩïÈªòËÆ§‰øùÁïô15Â§©ÔºåÊòüÊ†áÂêéÂèØÊ∞∏‰πÖ‰øùÁïô"
      >
        ‚≠ê
      </StarButton>
      <Input
        value={text}
        onChange={(e) => setText(e.target.value)}
        onKeyPress={handleKeyPress}
        placeholder="‰ªäÂ§©ÊÉ≥ËØ¥‰∫õ‰ªÄ‰πàÔºü"
      />
      <VisibilitySelect
        value={visibility}
        onChange={(e) => setVisibility(e.target.value as Visibility)}
      >
        <option value="private">‰ªÖËá™Â∑±ÂèØËßÅ</option>
        <option value="friends">Â•ΩÂèãÂèØËßÅ</option>
        <option value="broadcast">ÂåøÂêçÂπøÊí≠</option>
      </VisibilitySelect>
      <SendButton onClick={handleSubmit}>üöÄ</SendButton>
    </InputContainer>
  );
};
