// src/components/Moon.tsx
import React, { useState, useEffect, useMemo } from 'react';
import styled, { keyframes } from 'styled-components';
import { CelestialBody, Flower, EmotionType } from '../types';
import {
  moonImage,
  starIcon,
  meteorIcon,
  flower1Icon,
  flower2Icon,
  flower3Icon
} from '../assets';

// 月球自转动画
const rotate = keyframes`
  from { transform: rotate(0deg); }
  to   { transform: rotate(360deg); }
`;

// 星星/流星浮动动画
const float = keyframes`
  0%   { 
    opacity: 1;
    transform: translateY(0) scale(1); 
  }
  50%  { 
    transform: translateY(-15px) scale(1.1);
  }
  100% { 
    transform: translateY(0) scale(1);
  }
`;

const MoonContainer = styled.div`
  position: absolute;
  top: 50%; left: 50%;
  transform: translate(-50%, -50%);
  width: 300px; height: 300px;
  pointer-events: none;
`;

const MoonSphere = styled.div`
  width: 100%; height: 100%;
  border-radius: 50%;
  background: url(${moonImage}) no-repeat center center;
  background-size: cover;
  animation: ${rotate} 60s linear infinite;
  box-shadow: inset -25px -25px 40px rgba(0,0,0,0.3);
`;

// 覆盖在月球之上的花朵层，不跟随旋转
const FlowerOverlay = styled.div`
  position: absolute;
  top: 0; left: 0;
  width: 100%; height: 100%;
  pointer-events: auto;
`;

// 花朵点，使用 transient props 避免不当属性透传
interface FlowerDotProps {
  $x: number;
  $y: number;
  flowerType: 'flower1' | 'flower2' | 'flower3';
}
const FlowerDot = styled.div<FlowerDotProps>`
  position: absolute;
  left: ${p => p.$x}px;
  top: ${p => p.$y}px;
  width: 32px;
  height: 32px;
  background-size: contain;
  background-repeat: no-repeat;
  background-position: center;
  background-image: ${p =>
    p.flowerType === 'flower1'
      ? `url(${flower1Icon})`
      : p.flowerType === 'flower2'
      ? `url(${flower2Icon})`
      : `url(${flower3Icon})`
  };
`;

const CelestialBodyContainer = styled.div<{ x: number; y: number; type: EmotionType }>`
  position: absolute;
  left: ${p => p.x}px;
  top: ${p => p.y}px;
  width: 48px; height: 48px;
  background-size: contain;
  background-repeat: no-repeat;
  background-position: center;
  background-image: ${p =>
    p.type === 'positive'
      ? `url(${starIcon})`
      : p.type === 'negative'
      ? `url(${meteorIcon})`
      : `url(${starIcon})`};
  animation: ${float} 6s ease-in-out infinite;
  cursor: pointer;
  pointer-events: auto;

  &:hover {
    transform: scale(1.2);
    filter: brightness(1.2);
    z-index: 10;
  }
  transition: 
    left 0.5s ease-out,
    top 0.5s ease-out,
    transform 0.3s ease-out;

  // 禁用首次加载时的过渡
  &.initial {
    transition: none !important;
  }
`;

const Tooltip = styled.div`
  position: absolute;
  background: rgba(0,0,0,0.8);
  color: #fff;
  padding: 6px 10px;
  border-radius: 4px;
  font-size: 12px;
  max-width: 180px;
  pointer-events: none;
  opacity: 0;
  transition: opacity 0.2s;

  ${CelestialBodyContainer}:hover & {
    opacity: 1;
  }
`;

interface MoonProps {
  celestialBodies: CelestialBody[];
  flowers?: Flower[];
}

export const Moon: React.FC<MoonProps> = ({ celestialBodies, flowers = [] }) => {
  const [positions, setPositions] = useState<Record<string, { x: number; y: number }>>({});

  // 计算 overlay 相对于窗口中心的偏移
  const overlayOffset = useMemo(() => ({
    x: window.innerWidth / 2 - 150,
    y: window.innerHeight / 2 - 150
  }), []);

  useEffect(() => {
    const w = window.innerWidth;
    const h = window.innerHeight;
    const moonX = w / 2;
    const moonY = h / 2;
    const moonR = 150;
    const celestialSize = 48; // 星体的尺寸

    // 重新设计边界
    const topMargin = 120;    // 顶部留出更多空间给控制栏
    const bottomMargin = 180; // 底部留出更多空间给输入框
    const sideMargin = 100;   // 左右边距

    // 计算有效区域
    const minX = sideMargin;
    const maxX = w - sideMargin - celestialSize;
    const minY = topMargin;
    const maxY = h - bottomMargin - celestialSize;

    const safeDistance = moonR + 100; // 增加月球安全距离

    const newPos: Record<string, { x: number; y: number }> = {};
    celestialBodies.forEach(body => {
      if (positions[body.id]) {
        newPos[body.id] = positions[body.id];
      } else {
        let x: number, y: number;
        let cnt = 0;
        let angle: number;

        do {
          // 使用极坐标系统生成位置
          angle = Math.random() * Math.PI * 2; // 随机角度
          const minDist = safeDistance;
          const maxDist = Math.min(
            w / 2 - sideMargin,
            h / 2 - Math.max(topMargin, bottomMargin)
          );
          const distance = minDist + Math.random() * (maxDist - minDist);

          // 转换为笛卡尔坐标
          x = moonX + Math.cos(angle) * distance;
          y = moonY + Math.sin(angle) * distance;

          // 确保在有效区域内
          x = Math.max(minX, Math.min(maxX, x));
          y = Math.max(minY, Math.min(maxY, y));

          cnt++;
        } while (
          cnt < 100 && (
            // 检查是否与其他星体重叠
            Object.values(newPos).some(pos => 
              Math.hypot(x - pos.x, y - pos.y) < celestialSize * 2
            )
          )
        );

        newPos[body.id] = { x, y };
      }
    });

    setPositions(newPos);
    console.log('边界:', { minX, maxX, minY, maxY });
    console.log('生成的位置:', newPos);
  }, [celestialBodies]);

  // 修改浮动动画范围
  const float = keyframes`
    0%   { 
      transform: translateY(0) translateX(0);
    }
    50%  { 
      transform: translateY(-8px) translateX(5px); 
    }
    100% { 
      transform: translateY(0) translateX(0);
    }
  `;


  // 在CelestialBodyContainer添加固定定位层
  const PositionWrapper = styled.div`
    position: fixed;
    left: 0;
    top: 0;
    width: 100vw;
    height: 100vh;
    pointer-events: none;
  `;

  // 修改渲染结构
  return (
    <MoonContainer>
      <MoonSphere />
      <PositionWrapper>  {/* 新增固定定位层 */}
        {celestialBodies.map(body => (
          <CelestialBodyContainer
            key={body.id}
            x={positions[body.id]?.x || 0}
            y={positions[body.id]?.y || 0}
            type={body.type}
          >
            <Tooltip>{body.content}</Tooltip>
          </CelestialBodyContainer>
        ))}
      </PositionWrapper>
      {/* 原有FlowerOverlay保持不动 */}
    </MoonContainer>
  );
};
