    return EmotionAnalyzer.instance;
  }

  private debouncedAnalyze = debounce(async (text: string) => {
    return await this.actualAnalyze(text);
  }, 500); // é˜²æŠ–500ms

  public async analyzeEmotion(text: string): Promise<EmotionType> {
    return await this.debouncedAnalyze(text);
  }

  private async actualAnalyze(text: string): Promise<EmotionType> {
    if (!text.trim()) return 'received';

    // å¦‚æœç¼“å­˜æœ‰ï¼Œç›´æ¥è¿”å›ç¼“å­˜
    if (this.cache[text]) {
      console.log('[EmotionAnalyzer] ä½¿ç”¨ç¼“å­˜');
      return this.cache[text];
