æ–‡æœ¬å†…å®¹ï¼š
"${text}"
`;

      const response = await fetch(this.apiUrl, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${this.apiKey}`,
        },
        body: JSON.stringify({
          model: 'moonshot-v1-8k',
          messages: [{ role: 'user', content: prompt }],
          temperature: 0.2,
        }),
      });

      if (!response.ok) {
        console.error('æƒ…ç»ªè¯†åˆ«API HTTPé”™è¯¯:', response.status, response.statusText);
        if (response.status === 429) {
          alert('è¯·æ±‚å¤ªé¢‘ç¹å•¦ï¼Œè¯·ç¨ç­‰ä¸€ä¸‹ğŸŒ™');
        }
        return 'received';
      }

      const data = await response.json();
      const reply = data.choices?.[0]?.message?.content?.trim().toLowerCase();

      let result: EmotionType = 'received';
      if (reply?.includes('positive')) result = 'positive';
      else if (reply?.includes('negative')) result = 'negative';

      // ç¼“å­˜æœ¬æ¬¡ç»“æœ
      this.cache[text] = result;
      return result;

    } catch (error) {
      console.error('æƒ…ç»ªè¯†åˆ«å‡ºé”™:', error);
