import React, { useState } from 'react';
import styled, { keyframes } from 'styled-components';
import { CelestialBody, Flower, EmotionType } from '../types';
import { moonImage, starIcon, meteorIcon, flower1Icon, flower2Icon, flower3Icon } from '../assets';

const rotate = keyframes`
  from {
    transform: rotate(0deg);
  }
  to {
    transform: rotate(360deg);
  }
`;

const MoonContainer = styled.div`
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 300px;
  height: 300px;
`;

const MoonSphere = styled.div`
  width: 100%;
  height: 100%;
  border-radius: 50%;
  background: url(${moonImage}) no-repeat center center;
  background-size: cover;
  animation: ${rotate} 60s linear infinite;
  position: relative;
  box-shadow: inset -25px -25px 40px rgba(0,0,0,0.3);
`;

const CelestialBodyContainer = styled.div<{ x: number; y: number; type: EmotionType }>`
  position: absolute;
  left: ${props => props.x}px;
  top: ${props => props.y}px;
  cursor: pointer;
  transition: all 0.3s;
  width: 24px;
  height: 24px;
  background-size: contain;
  background-repeat: no-repeat;
  background-position: center;
  background-image: ${props => 
    props.type === 'positive' 
      ? `url(${starIcon})` 
      : props.type === 'negative' 
        ? `url(${meteorIcon})` 
        : `url(${starIcon})`};
  filter: ${props => props.type === 'received' ? 'hue-rotate(90deg)' : 'none'};

  &:hover {
    transform: scale(1.2);
    filter: brightness(1.2) ${props => props.type === 'received' ? 'hue-rotate(90deg)' : 'none'};
  }
`;

const FlowerContainer = styled.div<{ x: number; y: number; style: string }>`
  position: absolute;
  left: ${props => props.x}px;
  top: ${props => props.y}px;
  width: 32px;
  height: 32px;
  background-size: contain;
  background-repeat: no-repeat;
  background-position: center;
  background-image: ${props => {
    switch (props.style) {
      case 'flower1':
        return `url(${flower1Icon})`;
      case 'flower2':
        return `url(${flower2Icon})`;
      case 'flower3':
        return `url(${flower3Icon})`;
      default:
        return `url(${flower1Icon})`;
    }
  }};
`;

const Tooltip = styled.div`
  position: absolute;
  background: rgba(0, 0, 0, 0.8);
  color: white;
  padding: 8px 12px;
  border-radius: 4px;
  font-size: 14px;
  max-width: 200px;
  word-wrap: break-word;
  z-index: 1000;
  pointer-events: none;
  transition: opacity 0.3s;
  opacity: 0;

  ${CelestialBodyContainer}:hover & {
    opacity: 1;
  }
`;

interface MoonProps {
  celestialBodies: CelestialBody[];
  flowers?: Flower[];
}

export const Moon: React.FC<MoonProps> = ({ celestialBodies, flowers = [] }) => {
  const [hoveredBody, setHoveredBody] = useState<CelestialBody | null>(null);

  const renderCelestialBody = (body: CelestialBody) => {
    const hash = body.id.split('').reduce((acc, char) => acc + char.charCodeAt(0), 0);
    const angle = (hash % 360) * (Math.PI / 180);
    const radius = 200; // 轨道半径
    const x = Math.cos(angle) * radius;
    const y = Math.sin(angle) * radius;

    return (
      <CelestialBodyContainer
        key={body.id}
        x={x}
        y={y}
        type={body.type}
        onMouseEnter={() => setHoveredBody(body)}
        onMouseLeave={() => setHoveredBody(null)}
      >
        <Tooltip>
          {body.content}
        </Tooltip>
      </CelestialBodyContainer>
    );
  };

  return (
    <MoonContainer>
      <MoonSphere>
        {flowers.map(flower => (
          <FlowerContainer
            key={flower.id}
            x={flower.position.x}
            y={flower.position.y}
            style={flower.style}
          />
        ))}
      </MoonSphere>
      {celestialBodies.map(renderCelestialBody)}
    </MoonContainer>
  );
};
