// src/components/Moon.tsx
import React, { useState, useEffect, useMemo } from 'react';
import styled, { keyframes } from 'styled-components';
import { CelestialBody, Flower, EmotionType } from '../types';
import {
  moonImage,
  starIcon,
  meteorIcon,
  flower1Icon,
  flower2Icon,
  flower3Icon
} from '../assets';

// 月球自转动画
const rotate = keyframes`
  from { transform: rotate(0deg); }
  to   { transform: rotate(360deg); }
`;

// 星星/流星浮动动画
const float = keyframes`
  0%   { 
    opacity: 1;
    transform: translateY(0) scale(1); 
  }
  50%  { 
    transform: translateY(-15px) scale(1.1);
  }
  100% { 
    transform: translateY(0) scale(1);
  }
`;

const MoonContainer = styled.div`
  position: absolute;
  top: 50%; left: 50%;
  transform: translate(-50%, -50%);
  width: 300px; height: 300px;
  pointer-events: none;
`;

const MoonSphere = styled.div`
  width: 100%; height: 100%;
  border-radius: 50%;
  background: url(${moonImage}) no-repeat center center;
  background-size: cover;
  animation: ${rotate} 60s linear infinite;
  box-shadow: inset -25px -25px 40px rgba(0,0,0,0.3);
`;

// 覆盖在月球之上的花朵层，不跟随旋转
const FlowerOverlay = styled.div`
  position: absolute;
  top: 0; left: 0;
  width: 100%; height: 100%;
  pointer-events: auto;
`;

// 花朵点，使用 transient props 避免不当属性透传
interface FlowerDotProps {
  $x: number;
  $y: number;
  flowerType: 'flower1' | 'flower2' | 'flower3';
}
const FlowerDot = styled.div<FlowerDotProps>`
  position: absolute;
  left: ${p => p.$x}px;
  top: ${p => p.$y}px;
  width: 32px;
  height: 32px;
  background-size: contain;
  background-repeat: no-repeat;
  background-position: center;
  background-image: ${p =>
    p.flowerType === 'flower1'
      ? `url(${flower1Icon})`
      : p.flowerType === 'flower2'
      ? `url(${flower2Icon})`
      : `url(${flower3Icon})`
  };
`;

const CelestialBodyContainer = styled.div<{ x: number; y: number; type: EmotionType }>`
  position: absolute;
  left: ${p => p.x}px;
  top: ${p => p.y}px;
  width: 48px; height: 48px;
  background-size: contain;
  background-repeat: no-repeat;
  background-position: center;
  background-image: ${p =>
    p.type === 'positive'
      ? `url(${starIcon})`
      : p.type === 'negative'
      ? `url(${meteorIcon})`
      : `url(${starIcon})`};
  animation: ${float} 6s ease-in-out infinite;
  cursor: pointer;
  pointer-events: auto;

  &:hover {
    transform: scale(1.2);
    filter: brightness(1.2);
    z-index: 10;
  }
`;

const Tooltip = styled.div`
  position: absolute;
  background: rgba(0,0,0,0.8);
  color: #fff;
  padding: 6px 10px;
  border-radius: 4px;
  font-size: 12px;
  max-width: 180px;
  pointer-events: none;
  opacity: 0;
  transition: opacity 0.2s;

  ${CelestialBodyContainer}:hover & {
    opacity: 1;
  }
`;

interface MoonProps {
  celestialBodies: CelestialBody[];
  flowers?: Flower[];
}

export const Moon: React.FC<MoonProps> = ({ celestialBodies, flowers = [] }) => {
  const [positions, setPositions] = useState<Record<string, { x: number; y: number }>>({});

  // 计算 overlay 相对于窗口中心的偏移
  const overlayOffset = useMemo(() => ({
    x: window.innerWidth / 2 - 150,
    y: window.innerHeight / 2 - 150
  }), []);

  useEffect(() => {
    // 在位置计算完成后存储到sessionStorage
    const storedPositions = sessionStorage.getItem('celestialPositions');
    const initialPositions = storedPositions ? JSON.parse(storedPositions) : {};

    const w = window.innerWidth;
    const h = window.innerHeight;
    const moonX = w / 2, moonY = h / 2, moonR = 150;
    const margin = 50, topGap = 100, bottomGap = 150;
    // 修正后的可见区域计算
    const minX = margin;
    const maxX = w - margin - 48;
    const minY = topGap + margin; 
    const maxY = h - bottomGap - margin - 50; // 增加底部边距

    const newPos: Record<string, { x: number; y: number }> = {};
    celestialBodies.forEach(body => {
        if (positions[body.id]) {
            newPos[body.id] = positions[body.id];
        } else {
            let x: number, y: number, cnt = 0;
            do {
                // 使用更合理的随机分布算法
                x = Math.random() * (maxX - minX) + minX;
                y = Math.random() * (maxY - minY) + minY;
                cnt++;
            } while (cnt < 500 && (  // 增加最大尝试次数
                Math.hypot(x - moonX, y - moonY) < moonR + 30 || // 缩小避让半径
                x < minX || x > maxX ||  // 增加边界检查
                y < minY || y > maxY
            ));
            newPos[body.id] = { x, y };
        }
    });
    setPositions(newPos);

  // 在计算完成后存储新位置
  sessionStorage.setItem('celestialPositions', JSON.stringify(newPos));
  }, [celestialBodies]);

  return (
    <MoonContainer>
      <MoonSphere />

      <FlowerOverlay>
        {flowers.map(f => {
          const localX = f.position.x - overlayOffset.x;
          const localY = f.position.y - overlayOffset.y;
          return (
            <FlowerDot
              key={f.id}
              $x={localX}
              $y={localY}
              flowerType={f.style as 'flower1'|'flower2'|'flower3'}
            />
          );
        })}
      </FlowerOverlay>

      {celestialBodies.map(body => (
        <CelestialBodyContainer
          key={body.id}
          x={positions[body.id]?.x || 0}
          y={positions[body.id]?.y || 0}
          type={body.type}
        >
          <Tooltip>{body.content}</Tooltip>
        </CelestialBodyContainer>
      ))}
    </MoonContainer>
  );
};
