import React, { useState } from 'react';
import styled from 'styled-components';
import { Moon } from './components/Moon';
import { InputSection } from './components/InputSection';
import { ControlPanel } from './components/ControlPanel';
import { FlowerSelector } from './components/FlowerSelector';
import { CelestialBody, Flower, EmotionType } from './types';
import { backgroundImage, plantButtonIcon, radioIcon } from './assets';
import { EmotionAnalyzer } from './utils/EmotionAnalyzer';
import { toPng } from 'html-to-image';
import { EmotionChart } from './components/EmotionChart';

const AppContainer = styled.div`
  width: 100vw;
  height: 100vh;
  background: #000814;
  position: relative;
  overflow: hidden;
  display: flex;
  flex-direction: column;
`;

const Background = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: url(${backgroundImage}) no-repeat center center fixed;
  background-size: cover;
  z-index: 0;
  &:before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: radial-gradient(circle at center, rgba(0, 18, 51, 0.7) 0%, rgba(0, 8, 20, 0.9) 100%);
  }
`;

const ContentWrapper = styled.div`
  position: relative;
  z-index: 1;
  flex: 1;
  display: flex;
  flex-direction: column;
`;

// Áã¨Á´ãÁöÑÁßçËä±ÊåâÈíÆ
const PlantFlowerButton = styled.button`
  position: absolute;
  top: 100px;
  right: 20px;
  width: 110px;
  height: 40px;
  background: rgba(255, 255, 255, 0.1) url(${plantButtonIcon}) no-repeat center center;
  background-size: 110px 40px;
  border: none;
  cursor: pointer;
  border-radius: 20px;
  transition: all 0.3s;

  &:hover {
    transform: scale(1.1);
    background-color: rgba(255, 255, 255, 0.2);
  }
`;

const RadioButton = styled.button<{ isOn: boolean }>`
  position: absolute;
  top: 150px;
  right: 20px;
  width: 110px;
  height: 40px;
  background: rgba(255, 255, 255, 0.1) url(${radioIcon}) no-repeat center center;
  background-size: 110px 40px;
  border: none;
  cursor: pointer;
  border-radius: 20px;
  transition: all 0.3s;
  opacity: ${props => props.isOn ? 1 : 0.5};

  &:hover {
    transform: scale(1.1);
    background-color: rgba(255, 255, 255, 0.2);
  }
`;

export const App: React.FC = () => {
  const [celestialBodies, setCelestialBodies] = useState<CelestialBody[]>([]);
  const [flowers, setFlowers] = useState<Flower[]>([]);
  const [starCount, setStarCount] = useState(0);
  const [isRadioOn, setIsRadioOn] = useState(false);
  const [showFlowerSelector, setShowFlowerSelector] = useState(false);
  const [showChart, setShowChart] = useState(false);

  const handleEmotionSubmit = async (text: string, visibility: 'private' | 'friends' | 'broadcast', isStarred: boolean) => {
    // ‰ΩøÁî®‰ºòÂåñÂêéÁöÑÊÉÖÊÑüÂàÜÊûêÂô®
    const emotionAnalyzer = EmotionAnalyzer.getInstance();
    const emotionType = await emotionAnalyzer.analyzeEmotion(text);

    // ÂàõÂª∫Êñ∞ÁöÑÊòü‰Ωì
    const newCelestialBody: CelestialBody = {
      id: Date.now().toString(),
      type: emotionType,
      content: text,
      timestamp: Date.now(),
      isStarred,
      visibility
    };

    // Êõ¥Êñ∞Êòü‰ΩìÂàóË°®
    setCelestialBodies(prev => [...prev, newCelestialBody]);

    // Âè™ÊúâÊ≠£Èù¢ÊÉÖÁª™ÊâçÂ¢ûÂä†ÊòüÊòü
    if (emotionType === 'positive') {
      setStarCount(count => count + 1);
      console.log('Â¢ûÂä†ÊòüÊòüÔºåÂΩìÂâçÊÄªÊï∞:', starCount + 1);
    }
  };

  const handlePlantFlower = () => {
    setShowFlowerSelector(true);
  };

  const plantFlowerOnMoon = (flower: Flower) => {
    if (starCount < 3) {
      alert('‰Ω†ÈúÄË¶ÅËá≥Â∞ë 3 È¢óÊòüÊòüÊâçËÉΩÁßçËä± üåü');
      return;
    }
    setFlowers(prev => [...prev, flower]);
    setStarCount(prev => prev - 3);
  };

  const closeFlowerSelector = () => {
    setShowFlowerSelector(false);
  };

  const handleClear = () => {
    // ËøáÊª§Âá∫ÊòüÊ†áÂÜÖÂÆπ
    const starredBodies = celestialBodies.filter(body => body.isStarred);
    setCelestialBodies(starredBodies);
    setFlowers([]);

    // ËÆ°ÁÆóÊòüÊ†áÂÜÖÂÆπ‰∏≠ÁöÑÊ≠£Èù¢ÊÉÖÁª™Êï∞Èáè
    const positiveStarredCount = starredBodies.filter(body => body.type === 'positive').length;
    setStarCount(positiveStarredCount);
  };

  const handleExport = async () => {
    setShowChart(true);
    // Á≠âÂæÖÂõæË°®Ê∏≤ÊüìÂÆåÊàê
    setTimeout(async () => {
      const chartElement = document.querySelector('.chart-container');
      if (chartElement) {
        try {
          const dataUrl = await toPng(chartElement);
          // ÂàõÂª∫‰∏ãËΩΩÈìæÊé•
          const link = document.createElement('a');
          link.download = `ÊÉÖÁª™Êõ≤Á∫ø-${new Date().toLocaleDateString()}.png`;
          link.href = dataUrl;
          link.click();
        } catch (err) {
          console.error('ÂØºÂá∫Â§±Ë¥•:', err);
        }
      }
      setShowChart(false);
    }, 500);
  };

  return (
    <AppContainer>
      <Background />
      <ContentWrapper>
        <ControlPanel 
          starCount={starCount}
          onExport={handleExport}
          onClear={handleClear}
        />

        {/* Áã¨Á´ãÁöÑÁßçËä±ÊåâÈíÆÂíåÁîµÂè∞ÊåâÈíÆ */}
        <PlantFlowerButton 
          onClick={handlePlantFlower} 
          title="ÁßçËä±" 
          disabled={starCount < 3}
          style={{ opacity: starCount < 3 ? 0.5 : 1 }}
        />

        <RadioButton 
          onClick={() => setIsRadioOn(!isRadioOn)}
          isOn={isRadioOn}
          title="ÁîµÂè∞"
        />

        <Moon celestialBodies={celestialBodies} flowers={flowers} />
        <InputSection onSubmit={handleEmotionSubmit} />

        {showFlowerSelector && (
          <FlowerSelector
            onPlantFlower={plantFlowerOnMoon}
            onClose={closeFlowerSelector}
          />
        )}

        {showChart && (
          <EmotionChart
            celestialBodies={celestialBodies}
            onClose={() => setShowChart(false)}
          />
        )}
      </ContentWrapper>
    </AppContainer>
  );
};

export default App;
