// ÊîπËøõÁöÑÊÉÖÁª™Âà§Êñ≠ÂáΩÊï∞
const analyzeEmotion = (text: string): EmotionType => {
  // ËΩ¨Êç¢‰∏∫Â∞èÂÜôËøõË°åÂåπÈÖç
  const lowerText = text.toLowerCase();
  let positiveScore = 0;
  let negativeScore = 0;

  // Ê£ÄÊü•Ë°®ÊÉÖÁ¨¶Âè∑
  for (const emoji of EMOTION_CONFIG.emoticons.positive) {
    if (text.includes(emoji)) positiveScore += 1;
  }
  for (const emoji of EMOTION_CONFIG.emoticons.negative) {
    if (text.includes(emoji)) negativeScore += 1;
  }

  // ÂàÜËØçÂ§ÑÁêÜÔºàÁÆÄÂçïÊåâÁ©∫Ê†ºÂíåÊ†áÁÇπÂàÜËØçÔºâ
  const words = text.split(/[\s,.!?Ôºå„ÄÇÔºÅÔºü„ÄÅ]/);
  let hasNegation = false;
  let degreeMultiplier = 1;

  for (let i = 0; i < words.length; i++) {
    const word = words[i].toLowerCase();

    // Ê£ÄÊü•ÊòØÂê¶ÊúâÂê¶ÂÆöËØç
    if (EMOTION_CONFIG.negativePrefix.some(prefix => word.includes(prefix))) {
      hasNegation = !hasNegation; // ÂèåÈáçÂê¶ÂÆöÂèòËÇØÂÆö
      continue;
    }

    // Ê£ÄÊü•Á®ãÂ∫¶ËØç
    const degreeWord = Object.keys(EMOTION_CONFIG.degreeWords).find(degree => word.includes(degree));
    if (degreeWord) {
      degreeMultiplier = EMOTION_CONFIG.degreeWords[degreeWord];
      continue;
    }

    // ËÆ°ÁÆóÊÉÖÊÑüÂæóÂàÜ
    if (EMOTION_CONFIG.positiveWords.some(w => word.includes(w))) {
      const score = degreeMultiplier;
      positiveScore += hasNegation ? -score : score;
      hasNegation = false;
      degreeMultiplier = 1;
    }

    if (EMOTION_CONFIG.negativeWords.some(w => word.includes(w))) {
      const score = degreeMultiplier;
      negativeScore += hasNegation ? -score : score;
      hasNegation = false;
      degreeMultiplier = 1;
    }
  }

  // Ê†πÊçÆÂæóÂàÜÂà§Êñ≠ÊÉÖÁª™Á±ªÂûã
  if (positiveScore > negativeScore) {
    return 'positive';
  } else if (negativeScore > positiveScore) {
    return 'negative';
  } else {
    // Â¶ÇÊûúÂàÜÊï∞Áõ∏Á≠âÔºåÊ†πÊçÆÊñáÊú¨ÈïøÂ∫¶ÂíåÂÜÖÂÆπÁâπÂæÅËøõË°åÂêØÂèëÂºèÂà§Êñ≠
    const textLength = text.length;
    const hasExclamation = text.includes('!') || text.includes('ÔºÅ');
    const hasQuestion = text.includes('?') || text.includes('Ôºü');

    if (hasExclamation && !hasQuestion) {
      return 'positive'; // ÊÑüÂèπÂè•ÂÄæÂêë‰∫éË°®ËææÁßØÊûÅÊÉÖÁª™
    } else if (hasQuestion && !hasExclamation) {
      return 'negative'; // ÁñëÈóÆÂè•ÂÄæÂêë‰∫éË°®ËææÊ∂àÊûÅÊÉÖÁª™ÊàñÁñëËôë
    } else {
      // Â¶ÇÊûúËøòÊòØÊó†Ê≥ïÂà§Êñ≠Ôºå‰øùÊåÅÈöèÊú∫
      return Math.random() > 0.5 ? 'positive' : 'negative';
    }
  }
};

export const App: React.FC = () => {
  const [celestialBodies, setCelestialBodies] = useState<CelestialBody[]>([]);
  const [flowers, setFlowers] = useState<Flower[]>([]);
  const [starCount, setStarCount] = useState(0);
  const [isRadioOn, setIsRadioOn] = useState(false);
  const [showFlowerSelector, setShowFlowerSelector] = useState(false);

  const handleEmotionSubmit = (text: string, visibility: 'private' | 'friends' | 'broadcast', isStarred: boolean) => {
    // ÂàÜÊûêÊÉÖÁª™
    const emotionType = analyzeEmotion(text);

    // ÂàõÂª∫Êñ∞ÁöÑÊòü‰Ωì
    const newCelestialBody: CelestialBody = {
      id: Date.now().toString(),
      type: emotionType,
      content: text,
      timestamp: Date.now(),
      isStarred,
      visibility
    };

    // Êõ¥Êñ∞Êòü‰ΩìÂàóË°®
    setCelestialBodies(prev => [...prev, newCelestialBody]);

    // Â¶ÇÊûúÊòØÊ≠£Èù¢ÊÉÖÁª™ÔºåÂ¢ûÂä†ÊòüÊòüËÆ°Êï∞
    if (emotionType === 'positive') {
      setStarCount(prev => prev + 1);
    }

    // Â¶ÇÊûúÊ†áËÆ∞‰∏∫Ê∞∏‰πÖ‰øùÂ≠òÔºåÊó†ËÆ∫ÊÉÖÁª™Á±ªÂûãÈÉΩÂ¢ûÂä†ÊòüÊòüËÆ°Êï∞
    if (isStarred) {
      setStarCount(prev => prev + 1);
    }
  };

  const handlePlantFlower = () => {
    setShowFlowerSelector(true);
  };

  const plantFlowerOnMoon = (flower: Flower) => {
    setFlowers(prev => [...prev, flower]);
    if (starCount >= 3) {
      setStarCount(prev => prev - 3);
    }
  };

  const closeFlowerSelector = () => {
    setShowFlowerSelector(false);
  };

  const handleClear = () => {
    // Âè™Ê∏ÖÈô§ÈùûÊ∞∏‰πÖ‰øùÂ≠òÁöÑÊòü‰Ωì
    setCelestialBodies(prev => prev.filter(body => body.isStarred));
    setFlowers([]);
  };

  return (
    <AppContainer>
      <Background />
      <ContentWrapper>
        <ControlPanel 
          starCount={starCount}
          onExport={() => console.log('Export')}
          onClear={handleClear}
        />

        {/* Áã¨Á´ãÁöÑÁßçËä±ÊåâÈíÆÂíåÁîµÂè∞ÊåâÈíÆ */}
        <PlantFlowerButton 
          onClick={handlePlantFlower} 
          title="ÁßçËä±" 
          disabled={starCount < 3}
          style={{ opacity: starCount < 3 ? 0.5 : 1 }}
        />

        <RadioButton 
          onClick={() => setIsRadioOn(!isRadioOn)}
          isOn={isRadioOn}
          title="ÁîµÂè∞"
        />

        <Moon celestialBodies={celestialBodies} flowers={flowers} />
        <InputSection onSubmit={handleEmotionSubmit} />

        {showFlowerSelector && (
          <FlowerSelector
            onPlantFlower={plantFlowerOnMoon}
            onClose={closeFlowerSelector}
          />
        )}
      </ContentWrapper>
    </AppContainer>
  );
};

