import React, { useState, useEffect } from 'react';
import styled, { keyframes } from 'styled-components';
import { CelestialBody, Flower, EmotionType } from '../types';
import {
  moonImage,
  starIcon,
  meteorIcon,
  flower1Icon,
  flower2Icon,
  flower3Icon
} from '../assets';

const rotate = keyframes`
  from {
    transform: rotate(0deg);
  }
  to {
    transform: rotate(360deg);
  }
`;

const float = keyframes`
  0% { transform: translateY(0); }
  50% { transform: translateY(-10px); }
  100% { transform: translateY(0); }
`;

const MoonContainer = styled.div`
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 300px;
  height: 300px;
  z-index: 1;
`;

const MoonSphere = styled.div`
  width: 100%;
  height: 100%;
  border-radius: 50%;
  background: url(${moonImage}) no-repeat center center;
  background-size: cover;
  animation: ${rotate} 60s linear infinite;
  position: relative;
  box-shadow: inset -25px -25px 40px rgba(0,0,0,0.3);
`;

const CelestialBodyContainer = styled.div<{ x: number; y: number; type: EmotionType }>`
  position: absolute;
  left: ${props => props.x}px;
  top: ${props => props.y}px;
  cursor: pointer;
  width: 48px;
  height: 48px;
  background-size: contain;
  background-repeat: no-repeat;
  background-position: center;
  background-image: ${props =>
    props.type === 'positive'
      ? `url(${starIcon})`
      : props.type === 'negative'
        ? `url(${meteorIcon})`
        : `url(${starIcon})`};
  animation: ${float} 6s ease-in-out infinite;
  filter: ${props => props.type === 'received' ? 'hue-rotate(90deg)' : 'none'};

  &:hover {
    transform: scale(1.2);
    filter: brightness(1.2) ${props => props.type === 'received' ? 'hue-rotate(90deg)' : 'none'};
    z-index: 10;
  }
`;

const FlowerContainer = styled.div<{ x: number; y: number; style: string }>`
  position: absolute;
  left: ${props => props.x}px;
  top: ${props => props.y}px;
  width: 32px;
  height: 32px;
  background-size: contain;
  background-repeat: no-repeat;
  background-position: center;
  background-image: ${props => {
    switch (props.style) {
      case 'flower1':
        return `url(${flower1Icon})`;
      case 'flower2':
        return `url(${flower2Icon})`;
      case 'flower3':
        return `url(${flower3Icon})`;
      default:
        return `url(${flower1Icon})`;
    }
  }};
`;

const Tooltip = styled.div`
  position: absolute;
  background: rgba(0, 0, 0, 0.8);
  color: white;
  padding: 8px 12px;
  border-radius: 4px;
  font-size: 14px;
  max-width: 200px;
  word-wrap: break-word;
  z-index: 1000;
  pointer-events: none;
  transition: opacity 0.3s;
  opacity: 0;

  ${CelestialBodyContainer}:hover & {
    opacity: 1;
  }
`;

interface MoonProps {
  celestialBodies: CelestialBody[];
  flowers?: Flower[];
}

export const Moon: React.FC<MoonProps> = ({ celestialBodies, flowers = [] }) => {
  const [positions, setPositions] = useState<Record<string, { x: number; y: number }>>({});
  const [hoveredBody, setHoveredBody] = useState<CelestialBody | null>(null);
  const [windowSize, setWindowSize] = useState({
    width: window.innerWidth,
    height: window.innerHeight
  });

  // 监听窗口大小变化
  useEffect(() => {
    const handleResize = () => {
      setWindowSize({
        width: window.innerWidth,
        height: window.innerHeight
      });
    };

    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  useEffect(() => {
    const generatePositions = () => {
      const newPositions: Record<string, { x: number; y: number }> = {};

      // 根据窗口大小动态计算边界
      const padding = 100; // 边距
      const minX = padding;
      const maxX = windowSize.width - padding;
      const minY = padding;
      const maxY = windowSize.height - padding;

      // 月亮中心位置（在窗口中心）
      const moonCenterX = windowSize.width / 2;
      const moonCenterY = windowSize.height / 2;
      const moonRadius = 150;

      celestialBodies.forEach((body) => {
        if (positions[body.id]) {
          newPositions[body.id] = positions[body.id];
          return;
        }

        let x = moonCenterX;
        let y = moonCenterY;
        let validPosition = false;
        let attempt = 0;

        while (!validPosition && attempt < 50) {
          // 随机选择一个象限（1-4）
          const quadrant = Math.floor(Math.random() * 4) + 1;

          // 根据象限生成坐标
          switch (quadrant) {
            case 1: // 右上
              x = Math.random() * (maxX - moonCenterX) + moonCenterX;
              y = Math.random() * (moonCenterY - minY) + minY;
              break;
            case 2: // 左上
              x = Math.random() * (moonCenterX - minX) + minX;
              y = Math.random() * (moonCenterY - minY) + minY;
              break;
            case 3: // 左下
              x = Math.random() * (moonCenterX - minX) + minX;
              y = Math.random() * (maxY - moonCenterY) + moonCenterY;
              break;
            default: // 右下
              x = Math.random() * (maxX - moonCenterX) + moonCenterX;
              y = Math.random() * (maxY - moonCenterY) + moonCenterY;
          }

          // 检查是否与月亮重叠
          const distanceFromMoon = Math.sqrt(
            Math.pow(x - moonCenterX, 2) + Math.pow(y - moonCenterY, 2)
          );

          if (distanceFromMoon > moonRadius + 100) {
            validPosition = true;
          }

          attempt++;
        }

        // 确保位置在边界内
        x = Math.max(minX, Math.min(x, maxX));
        y = Math.max(minY, Math.min(y, maxY));

        newPositions[body.id] = { x, y };
      });

      setPositions(newPositions);
    };

    generatePositions();
  }, [celestialBodies, windowSize]);

  return (
    <MoonContainer>
      <MoonSphere>
        {flowers.map(flower => (
          <FlowerContainer
            key={flower.id}
            x={flower.position.x}
            y={flower.position.y}
            style={flower.style}
          />
        ))}
      </MoonSphere>
      {celestialBodies.map(body => (
        <CelestialBodyContainer
          key={body.id}
          x={positions[body.id]?.x || 0}
          y={positions[body.id]?.y || 0}
          type={body.type}
          onMouseEnter={() => setHoveredBody(body)}
          onMouseLeave={() => setHoveredBody(null)}
        >
          <Tooltip>{body.content}</Tooltip>
        </CelestialBodyContainer>
      ))}
    </MoonContainer>
  );
};
