import React, { useState, useEffect, useMemo } from 'react';
import styled, { keyframes } from 'styled-components';
import { CelestialBody, Flower, EmotionType } from '../types';
import {
  moonImage,
  starIcon,
  meteorIcon,
  flower1Icon,
  flower2Icon,
  flower3Icon,
  othersIcon
} from '../assets';
import { ReplyBox } from './ReplyBox';

const rotate = keyframes`
  from { transform: rotate(0deg); }
  to   { transform: rotate(360deg); }
`;

const float = keyframes`
  0%   { transform: translateY(0) translateX(0); }
  50%  { transform: translateY(-8px) translateX(5px); }
  100% { transform: translateY(0) translateX(0); }
`;

const MoonContainer = styled.div`
  position: absolute;
  top: 50%; left: 50%;
  transform: translate(-50%, -50%);
  width: 360px; height: 360px;
  pointer-events: none;
`;

const MoonSphere = styled.div`
  width: 100%; height: 100%;
  border-radius: 50%;
  background: url(${moonImage}) no-repeat center center;
  background-size: cover;
  animation: ${rotate} 60s linear infinite;
  box-shadow: inset -25px -25px 40px rgba(0,0,0,0.3);
`;

const FlowerOverlay = styled.div`
  position: absolute;
  top: 0; left: 0;
  width: 100%; height: 100%;
  pointer-events: auto;
`;

interface FlowerDotProps {
  $x: number;
  $y: number;
  flowerType: 'flower1' | 'flower2' | 'flower3';
}
const FlowerDot = styled.div<FlowerDotProps>`
  position: absolute;
  left: ${p => p.$x}px;
  top: ${p => p.$y}px;
  width: 32px;
  height: 32px;
  background-size: contain;
  background-repeat: no-repeat;
  background-position: center;
  background-image: ${p =>
    p.flowerType === 'flower1'
      ? `url(${flower1Icon})`
      : p.flowerType === 'flower2'
      ? `url(${flower2Icon})`
      : `url(${flower3Icon})`};
`;

const CelestialBodyContainer = styled.div<{
  type: EmotionType;
  isNew?: boolean;
  x: number;
  y: number;
  isExternal?: boolean;
}>`
  position: absolute;
  left: ${props => props.x}px;
  top: ${props => props.y}px;
  width: 30px;
  height: 30px;
  background-size: contain;
  background-repeat: no-repeat;
  cursor: pointer;
  transition: transform 0.3s ease-in-out;
  animation: ${props => props.isNew ? 'moveAway 5s ease-in-out forwards' : 'none'};

  ${props => {
    switch (props.type) {
      case 'positive':
        return `background-image: url(${starIcon});`;
      case 'negative':
        return `background-image: url(${meteorIcon});`;
      case 'received':
        return `
          background-image: url(${starIcon});
          filter: hue-rotate(120deg);
        `;
      default:
        return '';
    }
  }}

  &:hover {
    transform: scale(1.2);
  }

  @keyframes moveAway {
    0% {
      transform: translate(0, 0) scale(1);
      opacity: 1;
    }
    100% {
      transform: translate(100px, -100px) scale(0);
      opacity: 0;
    }
  }
`;

const Tooltip = styled.div`
  position: absolute;
  background: rgba(0,0,0,0.8);
  color: #fff;
  padding: 6px 10px;
  border-radius: 4px;
  font-size: 12px;
  max-width: 180px;
  pointer-events: none;
  opacity: 0;
  transition: opacity 0.2s;

  ${CelestialBodyContainer}:hover & {
    opacity: 1;
  }
`;

const PositionWrapper = styled.div`
  position: fixed;
  left: 0;
  top: 0;
  width: 100vw;
  height: 100vh;
  pointer-events: none;
`;

interface MoonProps {
  celestialBodies: CelestialBody[];
  flowers?: Flower[];
  isRadioOn?: boolean;
}

export const Moon: React.FC<MoonProps> = ({
  celestialBodies,
  flowers = [],
  isRadioOn = false
}) => {
  const [positions, setPositions] = useState<Record<string, { x: number; y: number }>>({});
  const [replyTo, setReplyTo] = useState<CelestialBody | null>(null);

  // ✅ 修正了原来的 moonCenter 计算逻辑
  const moonCenter = useMemo(() => {
    const topOffset = 120;
    const bottomOffset = 180;
    return {
      x: window.innerWidth / 2,
      y: (window.innerHeight - topOffset - bottomOffset) / 2 + topOffset
    };
  }, []);

  useEffect(() => {
    const w = window.innerWidth;
    const h = window.innerHeight;
    const moonR = 150;
    const celestialSize = 48;

    const moonX = moonCenter.x;
    const moonY = moonCenter.y;

    const topMargin = 120;
    const bottomMargin = 180;
    const sideMargin = 100;

    const minX = sideMargin;
    const maxX = w - sideMargin - celestialSize;
    const minY = topMargin;
    const maxY = h - bottomMargin - celestialSize;

    const safeDistance = moonR + 80;

    const newPos: Record<string, { x: number; y: number }> = {};
    celestialBodies.forEach(body => {
      if (positions[body.id]) {
        newPos[body.id] = positions[body.id];
      } else {
        let x: number, y: number, cnt = 0;
        let angle: number;
        do {
          angle = Math.random() * Math.PI * 2;
          const minDist = safeDistance;
          const maxDist = Math.min(
            Math.min(moonX - minX, maxX - moonX) * 1.2,
            Math.min(moonY - minY, maxY - moonY) * 2.7
          );
          const distance = minDist + Math.random() * (maxDist - minDist);
          x = moonX + Math.cos(angle) * distance;
          y = moonY + Math.sin(angle) * distance;
          x = Math.max(minX, Math.min(maxX, x));
          y = Math.max(minY, Math.min(maxY, y));
          cnt++;
        } while (
          cnt < 100 && (
            Object.values(newPos).some(pos =>
              Math.hypot(x - pos.x, y - pos.y) < celestialSize * 1.5
            )
          )
        );
        newPos[body.id] = { x, y };
      }
    });

    setPositions(newPos);
  }, [celestialBodies, moonCenter]);

  const handleCelestialBodyClick = (body: CelestialBody) => {
    if (body.isExternal && isRadioOn) {
      setReplyTo(body);
    }
  };

  const handleReply = (reply: string) => {
    console.log('回复消息:', reply);
    // 这里可以添加回复的处理逻辑
  };

  const visibleBodies = celestialBodies.filter(body => 
    !body.isExternal || (body.isExternal && isRadioOn)
  );

  return (
    <>
      <PositionWrapper>
        {visibleBodies.map(body => (
          <CelestialBodyContainer
            key={body.id}
            x={positions[body.id]?.x || moonCenter.x}
            y={positions[body.id]?.y || moonCenter.y}
            type={body.type}
            isExternal={body.isExternal}
            onClick={() => handleCelestialBodyClick(body)}
            isNew={body.isNew}
          >
            <Tooltip>{body.content}</Tooltip>
          </CelestialBodyContainer>
        ))}
      </PositionWrapper>

      <MoonContainer>
        <MoonSphere />
        <FlowerOverlay>
          {flowers.map(f => (
            <FlowerDot
              key={f.id}
              $x={f.position.x}
              $y={f.position.y}
              flowerType={f.style}
            />
          ))}
        </FlowerOverlay>
      </MoonContainer>

      {replyTo && (
        <ReplyBox
          content={replyTo.content}
          onClose={() => setReplyTo(null)}
          onSubmit={handleReply}
        />
      )}
    </>
  );
};
