import React, { useState } from 'react';
import styled from 'styled-components';
import { Moon } from './components/Moon';
import { InputSection } from './components/InputSection';
import { ControlPanel } from './components/ControlPanel';
import { CelestialBody, Flower, FlowerStyle, EmotionType } from './types';
import { backgroundImage, plantButtonIcon } from './assets';

const AppContainer = styled.div`
  width: 100vw;
  height: 100vh;
  background: #000814;
  position: relative;
  overflow: hidden;
  display: flex;
  flex-direction: column;
`;

const Background = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: url(${backgroundImage}) no-repeat center center fixed;
  background-size: cover;
  z-index: 0;
  &:before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: radial-gradient(circle at center, rgba(0, 18, 51, 0.7) 0%, rgba(0, 8, 20, 0.9) 100%);
  }
`;

const ContentWrapper = styled.div`
  position: relative;
  z-index: 1;
  flex: 1;
  display: flex;
  flex-direction: column;
`;

const FlowerStyleSelect = styled.div`
  position: fixed;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  background: rgba(255, 255, 255, 0.1);
  padding: 20px;
  border-radius: 10px;
  backdrop-filter: blur(5px);
  display: flex;
  flex-direction: column;
  gap: 15px;
  z-index: 100;
`;

const FlowerOption = styled.button<{ selected: boolean }>`
  background: ${props => props.selected ? 'rgba(255, 255, 255, 0.2)' : 'none'};
  border: 1px solid rgba(255, 255, 255, 0.3);
  padding: 10px 20px;
  border-radius: 5px;
  color: white;
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: 10px;

  &:hover {
    background: rgba(255, 255, 255, 0.1);
  }
`;

// Áã¨Á´ãÁöÑÁßçËä±ÊåâÈíÆ
const PlantFlowerButton = styled.button`
  position: absolute;
  top: 70px;
  right: 20px;
  width: 32px;
  height: 32px;
  background: none;
  border: none;
  cursor: pointer;
  padding: 0;
  border-radius: 10px;
  transition: all 0.3s;
  background-image: url(${plantButtonIcon});
  background-size: contain;
  background-repeat: no-repeat;
  background-position: center;

  &:hover {
    transform: scale(1.1);
    background-color: rgba(255, 255, 255, 0.1);
  }
`;

// ÁÆÄÂçïÁöÑÊÉÖÁª™Âà§Êñ≠ÂáΩÊï∞
const analyzeEmotion = (text: string): EmotionType => {
  // ÁßØÊûÅÊÉÖÁª™ÂÖ≥ÈîÆËØç
  const positiveWords = ['ÂºÄÂøÉ', 'Âø´‰πê', 'È´òÂÖ¥', 'ÂñúÊ¨¢', 'Áà±', 'Ê£í', 'Â•Ω', '‰ºòÁßÄ', 'ÊÑüÊÅ©', 
    'happy', 'love', 'great', 'good', 'excellent', 'wonderful', 'amazing', 'awesome'];

  // Ê∂àÊûÅÊÉÖÁª™ÂÖ≥ÈîÆËØç
  const negativeWords = ['ÈöæËøá', '‰º§ÂøÉ', 'ÁóõËã¶', 'ËÆ®Âéå', 'ÁÉ¶', 'Á≥üÁ≥ï', 'Â§±Êúõ', 'ÁÑ¶Ëôë', 
    'sad', 'hate', 'bad', 'terrible', 'awful', 'worried', 'anxious', 'depressed'];

  // ËΩ¨Êç¢‰∏∫Â∞èÂÜôËøõË°åÂåπÈÖç
  const lowerText = text.toLowerCase();

  // ÂåπÈÖçÂÖ≥ÈîÆËØç
  const positiveCount = positiveWords.filter(word => lowerText.includes(word)).length;
  const negativeCount = negativeWords.filter(word => lowerText.includes(word)).length;

  // Âà§Êñ≠ÊÉÖÁª™Á±ªÂûã
  if (positiveCount > negativeCount) {
    return 'positive';
  } else if (negativeCount > positiveCount) {
    return 'negative';
  } else {
    // Â¶ÇÊûúÊó†Ê≥ïÂà§Êñ≠ÊàñÁõ∏Á≠âÔºåÊ†πÊçÆÊñáÊú¨ÈïøÂ∫¶ÈöèÊú∫Âà§Êñ≠
    return Math.random() > 0.5 ? 'positive' : 'negative';
  }
};

export const App: React.FC = () => {
  const [celestialBodies, setCelestialBodies] = useState<CelestialBody[]>([]);
  const [flowers, setFlowers] = useState<Flower[]>([]);
  const [starCount, setStarCount] = useState(0);
  const [isRadioOn, setIsRadioOn] = useState(false);
  const [showFlowerSelect, setShowFlowerSelect] = useState(false);
  const [selectedFlowerStyle, setSelectedFlowerStyle] = useState<FlowerStyle>('flower1');

  const handleEmotionSubmit = (text: string, visibility: 'private' | 'friends' | 'broadcast', isStarred: boolean) => {
    // ÂàÜÊûêÊÉÖÁª™
    const emotionType = analyzeEmotion(text);

    // ÂàõÂª∫Êñ∞ÁöÑÊòü‰Ωì
    const newCelestialBody: CelestialBody = {
      id: Date.now().toString(),
      type: emotionType,
      content: text,
      timestamp: Date.now(),
      isStarred,
      visibility
    };

    // Êõ¥Êñ∞Êòü‰ΩìÂàóË°®
    setCelestialBodies(prev => [...prev, newCelestialBody]);

    // Â¶ÇÊûúÊòØÊ≠£Èù¢ÊÉÖÁª™ÔºåÂ¢ûÂä†ÊòüÊòüËÆ°Êï∞
    if (emotionType === 'positive') {
      setStarCount(prev => prev + 1);
    }

    // Â¶ÇÊûúÊ†áËÆ∞‰∏∫Ê∞∏‰πÖ‰øùÂ≠òÔºåÊó†ËÆ∫ÊÉÖÁª™Á±ªÂûãÈÉΩÂ¢ûÂä†ÊòüÊòüËÆ°Êï∞
    if (isStarred) {
      setStarCount(prev => prev + 1);
    }
  };

  const handlePlantFlower = () => {
    if (starCount >= 3) {
      setShowFlowerSelect(true);
    }
  };

  const confirmPlantFlower = (style: FlowerStyle) => {
    const newFlower: Flower = {
      id: Date.now().toString(),
      style,
      position: {
        x: Math.random() * 260 + 20, // 20-280px
        y: Math.random() * 260 + 20, // 20-280px
      }
    };
    setFlowers([...flowers, newFlower]);
    setStarCount(prev => prev - 3);
    setShowFlowerSelect(false);
  };

  const handleClear = () => {
    // Âè™Ê∏ÖÈô§ÈùûÊ∞∏‰πÖ‰øùÂ≠òÁöÑÊòü‰Ωì
    setCelestialBodies(prev => prev.filter(body => body.isStarred));
    setFlowers([]);
  };

  return (
    <AppContainer>
      <Background />
      <ContentWrapper>
        <ControlPanel 
          starCount={starCount}
          isRadioOn={isRadioOn}
          onRadioToggle={() => setIsRadioOn(!isRadioOn)}
          onPlantFlower={() => {}} // Á©∫ÂáΩÊï∞ÔºåÊéßÂà∂Ê†èÁöÑÁßçËä±ÊåâÈíÆ‰∏çÂÜç‰ΩøÁî®
          onExport={() => console.log('Export')}
          onClear={handleClear}
        />

        {/* Áã¨Á´ãÁöÑÁßçËä±ÊåâÈíÆ */}
        <PlantFlowerButton onClick={handlePlantFlower} title="ÁßçËä±" />

        <Moon celestialBodies={celestialBodies} flowers={flowers} />
        <InputSection onSubmit={handleEmotionSubmit} />

        {showFlowerSelect && (
          <FlowerStyleSelect>
            <h3 style={{ color: 'white', textAlign: 'center' }}>ÈÄâÊã©Ëä±ÊúµÊ†∑Âºè</h3>
            <FlowerOption
              selected={selectedFlowerStyle === 'flower1'}
              onClick={() => confirmPlantFlower('flower1')}
            >
              Ê†∑Âºè‰∏Ä üå∏
            </FlowerOption>
            <FlowerOption
              selected={selectedFlowerStyle === 'flower2'}
              onClick={() => confirmPlantFlower('flower2')}
            >
              Ê†∑Âºè‰∫å üå∫
            </FlowerOption>
            <FlowerOption
              selected={selectedFlowerStyle === 'flower3'}
              onClick={() => confirmPlantFlower('flower3')}
            >
              Ê†∑Âºè‰∏â üåπ
            </FlowerOption>
          </FlowerStyleSelect>
        )}
      </ContentWrapper>
    </AppContainer>
  );
};

export default App;
